import React, { useState } from "react";
import { Box, IconButton, TextField, Typography, ThemeProvider, createTheme } from "@mui/material";
import SendIcon from "@mui/icons-material/Send";

const theme = createTheme({
    palette: {
        primary: { main: "#309dff" },
        background: { default: "#f4f6f8" },
    },
    typography: { fontFamily: "Roboto, sans-serif" },
});

const Chatbot = () => {
    const [messages, setMessages] = useState([]);
    const [inputValue, setInputValue] = useState("");
    const [isSending, setIsSending] = useState(false);

    // üîπ Kullanƒ±cƒ± mesaj g√∂nderdiƒüinde √ßalƒ±≈üƒ±r
    const handleSendMessage = async () => {
        const trimmed = inputValue.trim();
        if (!trimmed || isSending) return;

        // Kullanƒ±cƒ± mesajƒ±nƒ± ekranda g√∂ster
        const newMessage = { sender: "user", text: trimmed };
        setMessages((prev) => [...prev, newMessage]);
        setInputValue("");
        setIsSending(true);

        try {
            const response = await fetch("http://localhost:5115/api/sentiment/analyze", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({ text: trimmed, lang: "tr" }),
            });

            if (!response.ok) throw new Error("Backend isteƒüi ba≈üarƒ±sƒ±z");

            const data = await response.json();
            console.log("Analiz sonucu:", data);

            // (ƒ∞stersen backend cevabƒ±nƒ± ekrana da g√∂sterebilirsin)
            // setMessages((prev) => [...prev, { sender: "bot", text: data.label_Tr || "Analiz tamamlandƒ±." }]);
        } catch (error) {
            console.error("Mesaj g√∂nderilirken hata olu≈ütu:", error);
        } finally {
            setIsSending(false);
        }
    };


    return (
        <ThemeProvider theme={theme}>
            <Box
                sx={{
                    width: "100%",
                    maxWidth: 600,
                    margin: "auto",
                    mt: 4,
                    p: 3,
                    borderRadius: 3,
                    backgroundColor: "white",
                    boxShadow: "0 4px 20px rgba(0,0,0,0.1)",
                    display: "flex",
                    flexDirection: "column",
                    gap: 2,
                }}
            >
                <Typography variant="h6" color="primary" sx={{ fontWeight: "bold" }}>
                    Chatbot
                </Typography>

                {/* Mesaj Alanƒ± */}
                <Box
                    sx={{
                        flexGrow: 1,
                        overflowY: "auto",
                        maxHeight: 400,
                        border: "1px solid #e0e0e0",
                        borderRadius: 2,
                        p: 2,
                        backgroundColor: "#fafafa",
                    }}
                >
                    {messages.map((msg, index) => (
                        <Box
                            key={index}
                            sx={{
                                display: "flex",
                                justifyContent: msg.sender === "user" ? "flex-end" : "flex-start",
                                mb: 1.5,
                            }}
                        >
                            <Box
                                sx={{
                                    bgcolor: msg.sender === "user" ? "#309dff" : "#e0e0e0",
                                    color: msg.sender === "user" ? "white" : "black",
                                    px: 2,
                                    py: 1,
                                    borderRadius: 2,
                                    maxWidth: "75%",
                                }}
                            >
                                {msg.text}
                            </Box>
                        </Box>
                    ))}
                </Box>

                {/* Giri≈ü ve G√∂nder Butonu */}
                <Box sx={{ display: "flex", alignItems: "center", gap: 1 }}>
                    <TextField
                        fullWidth
                        variant="outlined"
                        placeholder="Mesajƒ±nƒ±zƒ± yazƒ±n..."
                        value={inputValue}
                        onChange={(e) => setInputValue(e.target.value)}
                        onKeyPress={(e) => e.key === "Enter" && handleSendMessage()}
                        sx={{
                            "& .MuiOutlinedInput-root": {
                                "& fieldset": { borderColor: "rgba(48, 157, 255, 0.2)" },
                                "&:hover fieldset": { borderColor: "rgba(48, 157, 255, 0.4)" },
                                "&.Mui-focused fieldset": { borderColor: "#309dff" },
                            },
                        }}
                    />

                    <IconButton
                        color="primary"
                        onClick={handleSendMessage}
                        disabled={isSending || !inputValue.trim()}
                        sx={{
                            background: "linear-gradient(135deg, #309dff, #1e6dd0)",
                            color: "white",
                            width: 56,
                            height: 56,
                            borderRadius: "50%",
                            boxShadow: "0 4px 12px rgba(48, 157, 255, 0.4)",
                            transition: "all 0.3s ease",
                            "&:hover": {
                                background: "linear-gradient(135deg, #1e6dd0, #309dff)",
                                transform: "scale(1.05)",
                            },
                            "&:disabled": {
                                background: "rgba(48, 157, 255, 0.2)",
                            },
                        }}
                    >
                        <SendIcon />
                    </IconButton>
                </Box>
            </Box>
        </ThemeProvider>
    );
};

export default Chatbot;
