import React, { useState, useRef, useEffect } from 'react';
import {
    Box,
    Paper,
    Typography,
    TextField,
    IconButton,
    Avatar,
    Chip,
    CircularProgress,
    ThemeProvider,
    createTheme,
    Fade,
    Button,
} from '@mui/material';
import SendIcon from '@mui/icons-material/Send';
import PersonIcon from '@mui/icons-material/Person';

// Custom theme with #309dff color
const theme = createTheme({
    palette: {
        primary: {
            main: '#309dff',
            dark: '#1e6dd0',
        },
    },
});

const Chatbot = () => {
    const [messages, setMessages] = useState([]);
    const [inputValue, setInputValue] = useState('');
    const [isTyping, setIsTyping] = useState(false);
    const [showWelcome, setShowWelcome] = useState(true);
    const [totalMessages, setTotalMessages] = useState(0);
    const [currentLanguage, setCurrentLanguage] = useState('tr'); // Default language
    const messagesEndRef = useRef(null);
    const inputRef = useRef(null);

    // Language translations
    const translations = {
        tr: {
            headerTitle: 'Duygu Analiz Botu',
            statusText: 'Ã‡evrimiÃ§i â€¢ MesajlarÄ±nÄ±zÄ± analiz ediyor',
            welcomeTitle: 'Duygu Analiz Botuna HoÅŸ Geldiniz!',
            welcomeText: 'MesajlarÄ±nÄ±zÄ± yazÄ±n, ben de duygularÄ±nÄ±zÄ± analiz edeyim. Pozitif, negatif veya nÃ¶tr duygularÄ± tespit edebilirim.',
            statLabel: 'Analiz Edilen',
            placeholder: 'DuygularÄ±nÄ±zÄ± paylaÅŸÄ±n, analiz edeyim...',
            positive: 'Pozitif',
            negative: 'Negatif',
            neutral: 'NÃ¶tr',
            analysisResult: 'Analiz Sonucu:',
            analysisText: 'MesajÄ±nÄ±z',
            analysisText2: 'duygu iÃ§eriyor.',
            confidenceScore: 'GÃ¼ven skoru:'
        },
        en: {
            headerTitle: 'Emotion Analysis Bot',
            statusText: 'Online â€¢ Analyzing your messages',
            welcomeTitle: 'Welcome to Emotion Analysis Bot!',
            welcomeText: 'Write your messages, and I will analyze your emotions. I can detect positive, negative, or neutral emotions.',
            statLabel: 'Analyzed',
            placeholder: 'Share your feelings, I\'ll analyze them...',
            positive: 'Positive',
            negative: 'Negative',
            neutral: 'Neutral',
            analysisResult: 'Analysis Result:',
            analysisText: 'Your message contains',
            analysisText2: 'emotion.',
            confidenceScore: 'Confidence score:'
        }
    };

    // Emotion keywords in Turkish
    const positiveWordsTr = [
        'mutlu', 'sevinÃ§li', 'neÅŸeli', 'heyecanlÄ±', 'harika', 'mÃ¼kemmel',
        'gÃ¼zel', 'sÃ¼per', 'mÃ¼thiÅŸ', 'sevindim', 'teÅŸekkÃ¼r', 'saÄŸol',
        'iyi', 'baÅŸarÄ±lÄ±', 'baÅŸarÄ±', 'sevdim', 'beÄŸendim', 'wow', 'yay',
        'haha', 'hehe', 'gÃ¼le gÃ¼le', 'mutluluk', 'neÅŸe', 'umut', 'gurur', 'aÅŸk', 'sevgi'
    ];

    const negativeWordsTr = [
        'Ã¼zgÃ¼n', 'kÄ±zgÄ±n', 'sinirli', 'kÃ¶tÃ¼', 'berbat', 'aptal',
        'nefret', 'korku', 'endiÅŸe', 'kaygÄ±', 'stres', 'depresyon',
        'hayal kÄ±rÄ±klÄ±ÄŸÄ±', 'Ã¼zÃ¼ntÃ¼', 'acÄ±', 'aÄŸrÄ±', 'korkutucu', 'korkunÃ§',
        'dehÅŸet', 'tiksinti', 'Ã¶fke', 'sinir', 'frustrasyon', 'yorgun',
        'bÄ±kkÄ±n', 'sÄ±kÄ±ldÄ±m', 'canÄ±m sÄ±kÄ±ldÄ±', 'sÄ±kÄ±cÄ±', 'yorgunum', 'bÄ±ktÄ±m'
    ];

    // Emotion keywords in English
    const positiveWordsEn = [
        'happy', 'joyful', 'excited', 'great', 'wonderful', 'excellent',
        'amazing', 'fantastic', 'awesome', 'good', 'love', 'like', 'enjoy',
        'success', 'successful', 'thank', 'thanks', 'grateful', 'pleased',
        'delighted', 'thrilled', 'wonderful', 'perfect', 'beautiful', 'nice',
        'wow', 'yay', 'haha', 'lol', 'smile', 'laugh', 'cheerful', 'glad'
    ];

    const negativeWordsEn = [
        'sad', 'angry', 'mad', 'bad', 'terrible', 'awful', 'hate',
        'fear', 'worried', 'anxious', 'stress', 'depression', 'disappointed',
        'sorrow', 'pain', 'hurt', 'scary', 'horrible', 'terrible', 'disgust',
        'anger', 'frustrated', 'tired', 'bored', 'annoyed', 'upset', 'unhappy',
        'miserable', 'lonely', 'afraid', 'scared', 'frightened', 'nervous'
    ];

    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    };

    useEffect(() => {
        scrollToBottom();
    }, [messages, isTyping]);

    const getCurrentTime = () => {
        const now = new Date();
        const hours = String(now.getHours()).padStart(2, '0');
        const minutes = String(now.getMinutes()).padStart(2, '0');
        return `${hours}:${minutes}`;
    };

    const analyzeEmotion = (text) => {
        const lowerText = text.toLowerCase();
        let positiveCount = 0;
        let negativeCount = 0;

        // Select word lists based on current language
        const positiveWords = currentLanguage === 'tr' ? positiveWordsTr : positiveWordsEn;
        const negativeWords = currentLanguage === 'tr' ? negativeWordsTr : negativeWordsEn;

        // Count positive words
        positiveWords.forEach(word => {
            const regex = new RegExp(word, 'gi');
            const matches = lowerText.match(regex);
            if (matches) {
                positiveCount += matches.length;
            }
        });

        // Count negative words
        negativeWords.forEach(word => {
            const regex = new RegExp(word, 'gi');
            const matches = lowerText.match(regex);
            if (matches) {
                negativeCount += matches.length;
            }
        });

        // Check for exclamation marks and emojis
        const exclamationCount = (text.match(/!/g) || []).length;
        const positiveEmojis = (text.match(/[ðŸ˜€ðŸ˜ƒðŸ˜„ðŸ˜ðŸ˜†ðŸ˜ŠðŸ˜ðŸ¥°ðŸ˜˜ðŸ˜—ðŸ˜™ðŸ˜šâ˜ºï¸ðŸ™‚ðŸ¤—ðŸ¤©ðŸ¤”]/g) || []).length;
        const negativeEmojis = (text.match(/[ðŸ˜¢ðŸ˜­ðŸ˜¤ðŸ˜ ðŸ˜¡ðŸ¤¬ðŸ˜žðŸ˜ŸðŸ˜”ðŸ˜•ðŸ™â˜¹ï¸ðŸ˜£ðŸ˜–ðŸ˜«ðŸ˜©]/g) || []).length;

        positiveCount += positiveEmojis + (exclamationCount > 0 ? Math.min(exclamationCount, 2) : 0);
        negativeCount += negativeEmojis;

        // Get translations
        const t = translations[currentLanguage];

        // Determine emotion
        if (positiveCount > negativeCount && positiveCount > 0) {
            return {
                type: 'positive',
                confidence: Math.min(95, 50 + (positiveCount * 10)),
                score: positiveCount - negativeCount,
                icon: 'ðŸ˜Š',
                label: t.positive
            };
        } else if (negativeCount > positiveCount && negativeCount > 0) {
            return {
                type: 'negative',
                confidence: Math.min(95, 50 + (negativeCount * 10)),
                score: negativeCount - positiveCount,
                icon: 'ðŸ˜”',
                label: t.negative
            };
        } else {
            return {
                type: 'neutral',
                confidence: 60,
                score: 0,
                icon: 'ðŸ˜',
                label: t.neutral
            };
        }
    };

    const generateBotResponse = (emotion) => {
        const responses = {
            tr: {
                positive: [
                    'Harika! Mutlu olduÄŸunuzu gÃ¶rmek beni de sevindiriyor! ðŸ˜Š',
                    'Pozitif enerjiniz gerÃ§ekten bulaÅŸÄ±cÄ±! Size daha iyi gÃ¼nler dilerim! âœ¨',
                    'NeÅŸeli olduÄŸunuzu gÃ¶rmek Ã§ok gÃ¼zel! Bu pozitif enerjiyi koruyun! ðŸŒŸ',
                    'MutluluÄŸunuzu paylaÅŸtÄ±ÄŸÄ±nÄ±z iÃ§in teÅŸekkÃ¼rler! UmarÄ±m bu gÃ¼zel duygular devam eder! ðŸ’™'
                ],
                negative: [
                    'AnlÄ±yorum, zor bir dÃ¶nemden geÃ§iyor gibi gÃ¶rÃ¼nÃ¼yorsunuz. Her ÅŸey dÃ¼zelecek, umudunuzu kaybetmeyin. ðŸ’™',
                    'Bu duygularÄ± yaÅŸamanÄ±z normal. Kendinize zaman tanÄ±yÄ±n ve ihtiyacÄ±nÄ±z olursa yardÄ±m almaktan Ã§ekinmeyin. ðŸ¤—',
                    'ÃœzgÃ¼n olduÄŸunuzu anlÄ±yorum. Zor zamanlar geÃ§ici, gÃ¼zel gÃ¼nler mutlaka gelecek. ðŸŒˆ',
                    'DuygularÄ±nÄ±zÄ± anlÄ±yorum. Bazen paylaÅŸmak bile rahatlatÄ±cÄ± olabilir. YanÄ±nÄ±zdayÄ±m. ðŸ’™'
                ],
                neutral: [
                    'MesajÄ±nÄ±zÄ± analiz ettim. Duygusal durumunuz dengeli gÃ¶rÃ¼nÃ¼yor. ðŸ˜Š',
                    'MesajÄ±nÄ±zda belirgin bir pozitif veya negatif duygu tespit edemedim. BaÅŸka bir ÅŸey paylaÅŸmak ister misiniz?',
                    'Duygusal durumunuz nÃ¶tr gÃ¶rÃ¼nÃ¼yor. Her ÅŸey yolunda mÄ±?',
                    'MesajÄ±nÄ±zÄ± okudum. Daha detaylÄ± konuÅŸmak isterseniz, ben buradayÄ±m! ðŸ’™'
                ]
            },
            en: {
                positive: [
                    'Great! Seeing you happy makes me happy too! ðŸ˜Š',
                    'Your positive energy is really contagious! I wish you better days! âœ¨',
                    'It\'s so nice to see you cheerful! Keep this positive energy! ðŸŒŸ',
                    'Thank you for sharing your happiness! I hope these beautiful feelings continue! ðŸ’™'
                ],
                negative: [
                    'I understand, it seems like you\'re going through a difficult time. Everything will be okay, don\'t lose hope. ðŸ’™',
                    'It\'s normal to feel these emotions. Give yourself time and don\'t hesitate to seek help if you need it. ðŸ¤—',
                    'I understand you\'re sad. Difficult times are temporary, better days will definitely come. ðŸŒˆ',
                    'I understand your feelings. Sometimes sharing can be comforting. I\'m here for you. ðŸ’™'
                ],
                neutral: [
                    'I analyzed your message. Your emotional state seems balanced. ðŸ˜Š',
                    'I couldn\'t detect a clear positive or negative emotion in your message. Would you like to share something else?',
                    'Your emotional state appears neutral. Is everything okay?',
                    'I read your message. If you\'d like to talk in more detail, I\'m here! ðŸ’™'
                ]
            }
        };

        const responseArray = responses[currentLanguage][emotion.type] || responses[currentLanguage].neutral;
        return responseArray[Math.floor(Math.random() * responseArray.length)];
    };

    const changeLanguage = (lang) => {
        setCurrentLanguage(lang);
    };

    const handleSendMessage = () => {
        const messageText = inputValue.trim();

        if (!messageText || isTyping) {
            return;
        }

        // Hide welcome message on first message
        if (showWelcome) {
            setShowWelcome(false);
        }

        // Analyze emotion
        const emotion = analyzeEmotion(messageText);

        // Add user message
        const userMessage = {
            id: Date.now(),
            text: messageText,
            sender: 'user',
            time: getCurrentTime(),
            emotion: emotion
        };

        setMessages(prev => [...prev, userMessage]);
        setTotalMessages(prev => prev + 1);
        setInputValue('');
        setIsTyping(true);

        // Simulate bot thinking time (1.5-2.5 seconds)
        const delay = 1500 + Math.random() * 1000;

        setTimeout(() => {
            const botResponse = generateBotResponse(emotion);
            const botMessage = {
                id: Date.now() + 1,
                text: botResponse,
                sender: 'bot',
                time: getCurrentTime(),
                emotion: emotion
            };

            setMessages(prev => [...prev, botMessage]);
            setIsTyping(false);
            inputRef.current?.focus();
        }, delay);
    };

    const handleKeyPress = (e) => {
        if (e.key === 'Enter' && !e.shiftKey) {
            e.preventDefault();
            handleSendMessage();
        }
    };

    return (
        <ThemeProvider theme={theme}>
            <Box
                sx={{
                    width: '100%',
                    height: '100vh',
                    display: 'flex',
                    flexDirection: 'column',
                    background: 'linear-gradient(135deg, #309dff 0%, #1e6dd0 100%)',
                }}
            >
                {/* Header */}
                <Paper
                    elevation={0}
                    sx={{
                        background: 'rgba(255, 255, 255, 0.98)',
                        backdropFilter: 'blur(10px)',
                        padding: '20px 30px',
                        boxShadow: '0 4px 20px rgba(48, 157, 255, 0.2)',
                        borderBottom: '2px solid rgba(48, 157, 255, 0.1)',
                        borderRadius: 0,
                    }}
                >
                    <Box
                        sx={{
                            maxWidth: '1200px',
                            margin: '0 auto',
                            display: 'flex',
                            alignItems: 'center',
                            justifyContent: 'space-between',
                            gap: 2,
                            flexWrap: { xs: 'wrap', sm: 'nowrap' },
                        }}
                    >
                        <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                            <Avatar
                                sx={{
                                    width: 50,
                                    height: 50,
                                    background: 'linear-gradient(135deg, #309dff, #1e6dd0)',
                                    boxShadow: '0 4px 12px rgba(48, 157, 255, 0.4)',
                                }}
                            >
                                <PersonIcon />
                            </Avatar>
                            <Box>
                                <Typography
                                    variant="h6"
                                    sx={{
                                        fontWeight: 700,
                                        background: 'linear-gradient(135deg, #309dff, #1e6dd0)',
                                        WebkitBackgroundClip: 'text',
                                        WebkitTextFillColor: 'transparent',
                                        backgroundClip: 'text',
                                        margin: 0,
                                    }}
                                >
                                    {translations[currentLanguage].headerTitle}
                                </Typography>
                                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mt: 0.5 }}>
                                    <Box
                                        sx={{
                                            width: 8,
                                            height: 8,
                                            borderRadius: '50%',
                                            background: '#309dff',
                                            boxShadow: '0 0 8px rgba(48, 157, 255, 0.6)',
                                        }}
                                    />
                                    <Typography variant="body2" sx={{ color: '#666', fontSize: '13px' }}>
                                        {translations[currentLanguage].statusText}
                                    </Typography>
                                </Box>
                            </Box>
                        </Box>
                        <Box
                            sx={{
                                display: 'flex',
                                alignItems: 'center',
                                gap: 2,
                                width: { xs: '100%', sm: 'auto' },
                                justifyContent: { xs: 'space-between', sm: 'flex-end' },
                                order: { xs: -1, sm: 0 },
                            }}
                        >
                            {/* Language Selector */}
                            <Box
                                sx={{
                                    display: 'flex',
                                    gap: 1,
                                    background: 'rgba(48, 157, 255, 0.1)',
                                    padding: '4px',
                                    borderRadius: '12px',
                                }}
                            >
                                <Button
                                    onClick={() => changeLanguage('tr')}
                                    variant={currentLanguage === 'tr' ? 'contained' : 'text'}
                                    sx={{
                                        minWidth: 'auto',
                                        padding: '8px 16px',
                                        borderRadius: '8px',
                                        fontSize: '14px',
                                        fontWeight: 600,
                                        background: currentLanguage === 'tr' ? 'linear-gradient(135deg, #309dff, #1e6dd0)' : 'transparent',
                                        color: currentLanguage === 'tr' ? 'white' : '#666',
                                        boxShadow: currentLanguage === 'tr' ? '0 2px 8px rgba(48, 157, 255, 0.3)' : 'none',
                                        '&:hover': {
                                            background: currentLanguage === 'tr' ? 'linear-gradient(135deg, #1e6dd0, #309dff)' : 'rgba(48, 157, 255, 0.15)',
                                        },
                                    }}
                                >
                                    ðŸ‡¹ðŸ‡· TR
                                </Button>
                                <Button
                                    onClick={() => changeLanguage('en')}
                                    variant={currentLanguage === 'en' ? 'contained' : 'text'}
                                    sx={{
                                        minWidth: 'auto',
                                        padding: '8px 16px',
                                        borderRadius: '8px',
                                        fontSize: '14px',
                                        fontWeight: 600,
                                        background: currentLanguage === 'en' ? 'linear-gradient(135deg, #309dff, #1e6dd0)' : 'transparent',
                                        color: currentLanguage === 'en' ? 'white' : '#666',
                                        boxShadow: currentLanguage === 'en' ? '0 2px 8px rgba(48, 157, 255, 0.3)' : 'none',
                                        '&:hover': {
                                            background: currentLanguage === 'en' ? 'linear-gradient(135deg, #1e6dd0, #309dff)' : 'rgba(48, 157, 255, 0.15)',
                                        },
                                    }}
                                >
                                    ðŸ‡¬ðŸ‡§ EN
                                </Button>
                            </Box>
                            <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'flex-end' }}>
                                <Typography
                                    variant="caption"
                                    sx={{
                                        fontSize: '11px',
                                        color: '#999',
                                        textTransform: 'uppercase',
                                        letterSpacing: '0.5px',
                                    }}
                                >
                                    {translations[currentLanguage].statLabel}
                                </Typography>
                                <Typography
                                    variant="h6"
                                    sx={{
                                        fontWeight: 700,
                                        color: '#309dff',
                                        marginTop: '2px',
                                    }}
                                >
                                    {totalMessages}
                                </Typography>
                            </Box>
                        </Box>
                    </Box>
                </Paper>

                {/* Chat Container */}
                <Box
                    sx={{
                        flex: 1,
                        maxWidth: '1200px',
                        width: '100%',
                        margin: '0 auto',
                        backgroundColor: '#f5f7fa',
                        display: 'flex',
                        flexDirection: 'column',
                        height: 'calc(100vh - 120px)',
                        padding: '30px',
                        overflow: 'hidden',
                    }}
                >
                    {/* Welcome Message */}
                    {showWelcome && (
                        <Fade in={showWelcome}>
                            <Paper
                                elevation={0}
                                sx={{
                                    textAlign: 'center',
                                    padding: '40px 20px',
                                    background: 'white',
                                    borderRadius: '20px',
                                    marginBottom: '20px',
                                    boxShadow: '0 4px 15px rgba(48, 157, 255, 0.1)',
                                    border: '2px solid rgba(48, 157, 255, 0.1)',
                                }}
                            >
                                <Typography variant="h3" sx={{ marginBottom: 2 }}>
                                    ðŸ˜Š
                                </Typography>
                                <Typography
                                    variant="h5"
                                    sx={{
                                        fontWeight: 700,
                                        color: '#1a1a1a',
                                        marginBottom: 1,
                                    }}
                                >
                                    {translations[currentLanguage].welcomeTitle}
                                </Typography>
                                <Typography
                                    variant="body1"
                                    sx={{
                                        color: '#666',
                                        lineHeight: 1.6,
                                        maxWidth: '500px',
                                        margin: '0 auto',
                                    }}
                                >
                                    {translations[currentLanguage].welcomeText}
                                </Typography>
                            </Paper>
                        </Fade>
                    )}

                    {/* Messages */}
                    <Box
                        sx={{
                            display: 'flex',
                            flexDirection: 'column',
                            gap: 2.5,
                            overflowY: 'auto',
                            flex: 1,
                            padding: '20px 0',
                            paddingRight: '10px',
                            '&::-webkit-scrollbar': {
                                width: '8px',
                            },
                            '&::-webkit-scrollbar-track': {
                                background: 'rgba(0, 0, 0, 0.05)',
                                borderRadius: '4px',
                            },
                            '&::-webkit-scrollbar-thumb': {
                                background: 'rgba(0, 0, 0, 0.2)',
                                borderRadius: '4px',
                                '&:hover': {
                                    background: 'rgba(0, 0, 0, 0.3)',
                                },
                            },
                        }}
                    >
                        {messages.map((message) => (
                            <Fade key={message.id} in={true}>
                                <Box
                                    sx={{
                                        display: 'flex',
                                        alignItems: 'flex-end',
                                        gap: 1,
                                        justifyContent: message.sender === 'user' ? 'flex-end' : 'flex-start',
                                    }}
                                >
                                    {message.sender === 'bot' && (
                                        <Avatar
                                            sx={{
                                                width: 45,
                                                height: 45,
                                                background: 'linear-gradient(135deg, #309dff, #1e6dd0)',
                                                boxShadow: '0 4px 12px rgba(48, 157, 255, 0.4)',
                                            }}
                                        >
                                            <PersonIcon />
                                        </Avatar>
                                    )}
                                    <Box
                                        sx={{
                                            maxWidth: '65%',
                                            padding: '14px 18px',
                                            borderRadius: '20px',
                                            background:
                                                message.sender === 'user'
                                                    ? 'linear-gradient(135deg, #309dff, #1e6dd0)'
                                                    : 'white',
                                            color: message.sender === 'user' ? 'white' : '#333',
                                            borderBottomLeftRadius: message.sender === 'user' ? '4px' : '20px',
                                            borderBottomRightRadius: message.sender === 'user' ? '20px' : '4px',
                                            boxShadow:
                                                message.sender === 'user'
                                                    ? '0 4px 12px rgba(48, 157, 255, 0.3)'
                                                    : '0 2px 8px rgba(0, 0, 0, 0.08)',
                                            border:
                                                message.sender === 'bot'
                                                    ? '1px solid rgba(48, 157, 255, 0.1)'
                                                    : 'none',
                                        }}
                                    >
                                        <Typography
                                            variant="body1"
                                            sx={{
                                                fontSize: '15px',
                                                lineHeight: 1.5,
                                                marginBottom: 1,
                                            }}
                                        >
                                            {message.text}
                                        </Typography>
                                        {message.sender === 'bot' && message.emotion && (
                                            <Box
                                                sx={{
                                                    marginTop: 1.5,
                                                    padding: 1.5,
                                                    background: 'rgba(48, 157, 255, 0.05)',
                                                    borderRadius: '12px',
                                                    borderLeft: '3px solid #309dff',
                                                    fontSize: '13px',
                                                    lineHeight: 1.6,
                                                }}
                                            >
                                                <Typography variant="body2" sx={{ fontSize: '13px' }}>
                                                    <strong style={{ color: '#309dff' }}>{translations[currentLanguage].analysisResult}</strong>{' '}
                                                    {translations[currentLanguage].analysisText}{' '}
                                                    <strong style={{ color: '#309dff' }}>{message.emotion.label}</strong>{' '}
                                                    {translations[currentLanguage].analysisText2}{' '}
                                                    {translations[currentLanguage].confidenceScore}{' '}
                                                    <strong style={{ color: '#309dff' }}>%{message.emotion.confidence}</strong>
                                                </Typography>
                                            </Box>
                                        )}
                                        <Typography
                                            variant="caption"
                                            sx={{
                                                fontSize: '12px',
                                                opacity: 0.85,
                                                textAlign: 'right',
                                                marginTop: 1,
                                                display: 'block',
                                                color: message.sender === 'user' ? 'rgba(255, 255, 255, 0.9)' : '#666',
                                            }}
                                        >
                                            {message.time}
                                        </Typography>
                                    </Box>
                                </Box>
                            </Fade>
                        ))}

                        {/* Typing Indicator */}
                        {isTyping && (
                            <Box
                                sx={{
                                    display: 'flex',
                                    alignItems: 'flex-end',
                                    gap: 1,
                                    justifyContent: 'flex-start',
                                }}
                            >
                                <Avatar
                                    sx={{
                                        width: 45,
                                        height: 45,
                                        background: 'linear-gradient(135deg, #309dff, #1e6dd0)',
                                        boxShadow: '0 4px 12px rgba(48, 157, 255, 0.4)',
                                    }}
                                >
                                    <PersonIcon />
                                </Avatar>
                                <Paper
                                    elevation={0}
                                    sx={{
                                        padding: '12px 16px',
                                        borderRadius: '20px',
                                        borderBottomRightRadius: '4px',
                                        background: 'white',
                                        border: '1px solid rgba(48, 157, 255, 0.1)',
                                    }}
                                >
                                    <Box sx={{ display: 'flex', gap: 0.5 }}>
                                        {[0, 1, 2].map((index) => (
                                            <Box
                                                key={index}
                                                sx={{
                                                    width: 8,
                                                    height: 8,
                                                    borderRadius: '50%',
                                                    background: '#309dff',
                                                    opacity: 0.7,
                                                    animation: 'typing 1.4s infinite',
                                                    animationDelay: `${index * 0.2}s`,
                                                    '@keyframes typing': {
                                                        '0%, 60%, 100%': {
                                                            transform: 'translateY(0)',
                                                            opacity: 0.7,
                                                        },
                                                        '30%': {
                                                            transform: 'translateY(-10px)',
                                                            opacity: 1,
                                                        },
                                                    },
                                                }}
                                            />
                                        ))}
                                    </Box>
                                </Paper>
                            </Box>
                        )}
                        <div ref={messagesEndRef} />
                    </Box>

                    {/* Input Container */}
                    <Box
                        sx={{
                            display: 'flex',
                            gap: 1.5,
                            padding: '20px 0 0 0',
                            marginTop: 2.5,
                            borderTop: '1px solid rgba(0, 0, 0, 0.08)',
                        }}
                    >
                        <TextField
                            inputRef={inputRef}
                            fullWidth
                            placeholder={translations[currentLanguage].placeholder}
                            value={inputValue}
                            onChange={(e) => setInputValue(e.target.value)}
                            onKeyPress={handleKeyPress}
                            disabled={isTyping}
                            sx={{
                                '& .MuiOutlinedInput-root': {
                                    borderRadius: '28px',
                                    background: 'white',
                                    border: '2px solid rgba(48, 157, 255, 0.2)',
                                    '&:hover': {
                                        borderColor: 'rgba(48, 157, 255, 0.4)',
                                    },
                                    '&.Mui-focused': {
                                        borderColor: '#309dff',
                                        boxShadow: '0 4px 15px rgba(48, 157, 255, 0.2)',
                                    },
                                    '& fieldset': {
                                        border: 'none',
                                    },
                                },
                                '& .MuiInputBase-input': {
                                    padding: '16px 20px',
                                    fontSize: '15px',
                                },
                            }}
                        />
                        <IconButton
                            onClick={handleSendMessage}
                            disabled={!inputValue.trim() || isTyping}
                            sx={{
                                width: 52,
                                height: 52,
                                background: 'linear-gradient(135deg, #309dff, #1e6dd0)',
                                color: 'white',
                                boxShadow: '0 4px 12px rgba(48, 157, 255, 0.4)',
                                '&:hover': {
                                    background: 'linear-gradient(135deg, #1e6dd0, #309dff)',
                                    transform: 'scale(1.08)',
                                    boxShadow: '0 6px 18px rgba(48, 157, 255, 0.5)',
                                },
                                '&:active': {
                                    transform: 'scale(0.96)',
                                },
                                '&.Mui-disabled': {
                                    opacity: 0.5,
                                    transform: 'none',
                                },
                            }}
                        >
                            <SendIcon />
                        </IconButton>
                    </Box>
                </Box>
            </Box>
        </ThemeProvider>
    );
};

export default Chatbot;
