import React from "react";

const MessageList = ({ messages }) => {
    return (
        <div style={styles.list}>
            {messages.map((msg, index) => (
                <div key={index} style={styles.item}>
                    <strong>{msg.userNickname || "Anon"}:</strong> {msg.text}
                    {msg.sentiment && (
                        <span style={{ ...styles.sentiment, ...getSentimentColor(msg.sentiment) }}>
                            {msg.sentiment}
                        </span>
                    )}
                </div>
            ))}
        </div>
    );
};

const getSentimentColor = (sentiment) => {
    if (sentiment === "positive") return { backgroundColor: "#c8e6c9" };
    if (sentiment === "negative") return { backgroundColor: "#ffcdd2" };
    return { backgroundColor: "#e0e0e0" };
};

const styles = {
    list: {
        height: "300px",
        overflowY: "auto",
        border: "1px solid #ddd",
        borderRadius: "8px",
        padding: "10px",
        backgroundColor: "#fff",
    },
    item: {
        marginBottom: "8px",
        padding: "6px",
        borderRadius: "6px",
        backgroundColor: "#fafafa",
    },
    sentiment: {
        marginLeft: "8px",
        padding: "2px 6px",
        borderRadius: "6px",
        fontSize: "12px",
    }
};

export default MessageList;
