import React, { useEffect, useState } from "react";
import * as signalR from "@microsoft/signalr";
import { API_URL, SIGNALR_URL } from "../config";
import MessageList from "./MessageList";

const Chat = () => {
    const [connection, setConnection] = useState(null);
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState("");
    const [nickname, setNickname] = useState("");

    // KullanÄ±cÄ± takma adÄ±nÄ± al
    useEffect(() => {
        const savedNick = localStorage.getItem("nickname");
        if (savedNick) {
            setNickname(savedNick);
        } else {
            const nick = prompt("Takma adÄ±nÄ±zÄ± girin:");
            localStorage.setItem("nickname", nick);
            setNickname(nick);
        }
    }, []);

    // SignalR baÄŸlantÄ±sÄ±nÄ± baÅŸlat
    useEffect(() => {
        const connect = new signalR.HubConnectionBuilder()
            .withUrl(SIGNALR_URL)
            .withAutomaticReconnect()
            .build();

        connect.start()
            .then(() => console.log("SignalR baÄŸlantÄ±sÄ± kuruldu."))
            .catch(err => console.error("SignalR baÄŸlantÄ± hatasÄ±:", err));

        // Yeni mesaj geldiÄŸinde
        connect.on("NewMessage", (message) => {
            setMessages(prev => [...prev, message]);
        });

        setConnection(connect);
    }, []);

    // Mesaj gÃ¶nderme fonksiyonu
    const sendMessage = async () => {
        if (!input.trim()) return;

        try {
            const res = await fetch(`${API_URL}/messages`, {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ userNickname: nickname, text: input })
            });
            if (res.ok) setInput("");
        } catch (err) {
            console.error("Mesaj gÃ¶nderme hatasÄ±:", err);
        }
    };

    return (
        <div style={styles.container}>
            <h2 style={styles.title}>ðŸ’¬ GerÃ§ek ZamanlÄ± Sohbet</h2>
            <MessageList messages={messages} />
            <div style={styles.inputContainer}>
                <input
                    style={styles.input}
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    placeholder="MesajÄ±nÄ±zÄ± yazÄ±n..."
                />
                <button style={styles.button} onClick={sendMessage}>GÃ¶nder</button>
            </div>
        </div>
    );
};

const styles = {
    container: {
        width: "400px",
        margin: "50px auto",
        padding: "20px",
        borderRadius: "12px",
        backgroundColor: "#f5f5f5",
        boxShadow: "0 0 10px rgba(0,0,0,0.1)"
    },
    title: { textAlign: "center" },
    inputContainer: { display: "flex", marginTop: "10px" },
    input: {
        flex: 1,
        padding: "10px",
        borderRadius: "8px",
        border: "1px solid #ccc",
    },
    button: {
        marginLeft: "8px",
        padding: "10px 16px",
        border: "none",
        borderRadius: "8px",
        backgroundColor: "#1976d2",
        color: "#fff",
        cursor: "pointer",
    }
};

export default Chat;
